import React from 'react';
import ReactDOM from 'react-dom';
import {Route, NavLink} from 'react-router-dom';
import { JsonToTable } from "react-json-to-table";
import customerService from '../Services/customerService';
import UserMenu from './UserMenu';
import ViewCustomer from './ViewCustomer';
import Menu, {Item as MenuItem} from 'rc-menu';
import '../Menu.css';
import '../customers.css';
import '../index.css';

class ViewCustomers extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          user: {},
          customers: [],
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);

  }
  componentWillMount()
  {
    customerService.getCustomers()
  }

  routeMenu() {
    ReactDOM.render(<UserMenu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

  render(){
    var menuStyle = {
      margin: 'auto',
      padding: 40,
     // width: 700,
      height: 400,
      overflow: 'auto',
    };
    return (
        
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Customers</p>
          </header>
          <button onClick={this.routeMenu}  className="btn btn-primary">Menu</button>
              <Menu className='scrollList'>
              {JSON.parse(localStorage.customers).map((data, i) => {
                  return (
                      <tr key={i}>
                          <td>{data.customer_id}</td>
                          <td ><NavLink to="/customer/{customer_id}">{data.customer_name}</NavLink></td>
                      </tr>
                  )})
                  }
              
              </Menu>
          
        </div>
      );
    }
  }
  

export default ViewCustomers;
