import React from 'react';
import '../Menu.css';

class scanBarcode extends React.Component{
  constructor(props)
  {
      super(props);
      this.state = {
          user: {}
      };
  }

  render(){
    
    const { user } = this.state;
    return (
      <div>
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>Welcome, {localStorage.getItem('username')}</p>
          </header>
            <div>
                Scanning Barcode:
            </div>
            <div>
            <ReactTable
            getTrGroupProps={(state, rowInfo) => {
            if (rowInfo !== undefined) {
              return {
                  onClick: () => {
                    var path = "/customer/"+rowInfo.row.customer_id
                    this.props.history.push(path);
                  },
                  style: {
                      cursor: 'pointer',
                      background: rowInfo.original.id === this.state.selectedIndex ? '#00afec' : 'white',
                      color: rowInfo.original.id === this.state.selectedIndex ? 'white' : 'black'
                              }
                          }
                    }}
                }

              data={customers}
              columns={[
                {
                  Header: "Customer ID",
                  accessor: "customer_id"
                },
                {
                  Header: "Customer Name",
                  accessor:"customer_name"
                }
              ]}
              filterable
              defaultPageSize={10}
              className="-striped -highlight"
            >
              {(state, makeTable) => {
                  return (
                    <div>
                      {makeTable()}
                    </div>
                  )
                }}
              </ReactTable>
            </div>
        </div>
      </div>
      );
    }
  }
  

export default scanBarcode;
