import React from 'react';
import ReactDOM from 'react-dom';
import { JsonToTable } from "react-json-to-table";
import customerService from '../Services/customerService';
import UserMenu from './UserMenu';
import Menu, {Item as MenuItem} from 'rc-menu';
import '../Menu.css';
import '../customers.css';

class ViewCustomer extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          user: {},
          customer: [],
          jobs: [],
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);

  }
  componentWillMount()
  {
    customerService.getCustomerByID()
  }

  routeMenu() {
    ReactDOM.render(<UserMenu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    

  render(){
    var menuStyle = {
      margin: 'auto',
      padding: 50,
     // width: 700,
      height: 400,
      overflow: 'auto',
    };
    
    return (
        
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Customer</p>
          </header>
          <button onClick={this.routeMenu}  className="btn btn-primary">Menu</button>
                <JsonToTable json={JSON.parse(localStorage.customer)} />
              <Menu style={menuStyle}>
              <JsonToTable json={JSON.parse(localStorage.customerJobs)} />
              </Menu>
          
        </div>
      );
    }
  }
  

export default ViewCustomer;
