import React from "react";
import { Bar } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";

export default class LocationChart extends React.Component {
    componentDidMount()
    {
        this.getBoxLocations()
    }
    getLocations()
    {
        {this.state.stats.map((data, i) => {
            alert(data.LocNums)
            return [4, 5, 6, 7]
            })}
    }
    chart = {
        dataBar: {
            labels: ["Preparation", "Scanning", "Quality Control", "Dispatch"],
            datasets: [
                {
                    label: "No. Of Boxes",
                    data: [4, 5, 6, 7],
                    backgroundColor: [
                        "rgba(113, 205, 205,0.4)",
                        "rgba(98,  182, 239,0.4)",
                        "rgba(255, 177, 101,0.4)",
                        "rgba(255, 134,159,0.4)"

                    ],
                    borderWidth: 2,
                    borderColor: [
                        "rgba(113, 205, 205, 1)",
                        "rgba(98,  182, 239, 1)",
                        "rgba(255, 177, 101, 1)",
                        "rgba(255, 134, 159, 1)"

                    ]
                }
            ]
        },
        barChartOptions: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
                xAxes: [
                    {
                        barPercentage: 1,
                        gridLines: {
                            display: true,
                            color: "rgba(0, 0, 0, 0.1)"
                        }
                    }
                ],
                yAxes: [
                    {
                        gridLines: {
                            display: true,
                            color: "rgba(0, 0, 0, 0.1)"
                        },
                        ticks: {
                            beginAtZero: true
                        }
                    }
                ]
            }
        }
    }


    render() {
        return (
            <MDBContainer>
                <h3 className="mt-5">Number of Boxes Per Location</h3>
                <Bar data={this.chart.dataBar} options={this.chart.barChartOptions} />
            </MDBContainer>
        );
    }
}
