import React from 'react';
import ReactDOM from 'react-dom';
import ReactScrollList from 'react-scroll-list'
import customerService from './customerService';
import Menu from './Menu';
import './Menu.css';
import './customers.css';

class AddCustomer extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          user: {},
          customers: {},
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);

  }

  componentWillMount()
  {
    this.getCustomers()
  }

  getCustomers(){
    customerService.getCustomers()
  }

  routeMenu() {
    ReactDOM.render(<Menu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }
  
  render(){
    const { user, customers, submitted, loading } = this.state;
    const list = localStorage.customers.map((item, ID) =>
    <li key={ID}>
        <p>{`Customer ID: ${item.customer_id}`}</p>
        <p>{`Customer Name: ${item.customer_name}`}</p>
        <p>{`Customer Email: ${item.customer_email}`}</p></li>)
    return (
        
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Customers</p>
          </header>
          <body>

            <ul>
                {list}
            </ul>

            <pre className = "customer">{JSON.stringify(JSON.parse(localStorage.customers), null, 2)}</pre>
              <button onClick={this.routeMenu}  className="btn btn-primary">Menu</button>
          </body>
        </div>
      );
    }
  }
  

export default AddCustomer;
