import React from 'react';
import ReactDOM from 'react-dom';
import JSPretty from 'react-json-pretty';
import customerService from './customerService';
import UserMenu from './UserMenu';
import Menu, {Item as MenuItem} from 'rc-menu';
import './Menu.css';
import './customers.css';

class AddCustomer extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          user: {},
          customers: {},
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);

  }

  componentWillMount()
  {
    this.getCustomers()
  }

  getCustomers(){
    customerService.getCustomers()
  }

  routeMenu() {
    ReactDOM.render(<UserMenu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    
  render(){
    const { user, customers, submitted, loading } = this.state;
    var menuStyle = {
      width: 800,
      height: 500,
      overflow: 'auto',
    };
    return (
        
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Customers</p>
          </header>
          <body>
          <button onClick={this.routeMenu}  className="btn btn-primary">Menu</button>
          <Menu style={menuStyle}>{
            localStorage.customers
            }</Menu>
              
          </body>
        </div>
      );
    }
  }
  

export default AddCustomer;
