import React from 'react';
import ReactDOM from 'react-dom';
import customerService from './customerService';
import Menu from './Menu';
import './Menu.css';

class AddCustomer extends React.Component{
  constructor(props)
  {
      super(props);
      this.state = {
          user: {},
          customer_name: '',
          submitted: false,
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  componentDidMount() {
    window.onpopstate = this.handlePopState;
    this.applyParams();
  }

  componentWillUnmount() {
    window.onpopstate = null;
  }

  handlePopState = (event) => {
    event.preventDefault();
    this.applyParams();
  }

  applyParams() {
    this.params = this.props.location.state || {};
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e)
    {
        e.preventDefault();
        this.setState({ submitted: true });
        const { customer_name, submitted,returnUrl } = this.state;
  
        // stop here if form is invalid
        if (!(customer_name))
        {
            return;
        }
  
        this.setState({ loading: true });
        customerService.addCustomer(customer_name)
    }
  
  render(){
    const { user, customer_name, submitted, loading } = this.state;
    return (
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>Add New Customer</p>
          </header>
          <body className="Menu-body">
          <form name="form" onSubmit={this.handleSubmit}>
          <div className={'form-group' + (submitted && !customer_name ? ' has-error' : '')}>
              <label htmlFor="customer_name">Customer Name:</label>
              <input
                type="text"
                className="form-control"
                name="customer_name"
                value={customer_name}
                onChange={this.handleChange} />
          </div>
          <div className="form-group">
              <button className="btn btn-primary" disabled={loading}>Create</button>
              {loading &&
                  <img src="data:image/gif;base64," />
              }
          </div>
          </form>
          </body>
        </div>
      );
    }
  }
  

export default AddCustomer;
