import React from 'react';
import ReactDOM from 'react-dom';
import { withRouter } from "react-router";
import jobService from '../Services/jobService';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import '../Menu.css';
import '../index.css'

const propTypes ={
  location: PropTypes.object.isRequired
}
const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

class AddJob extends React.Component{
 
  constructor(props)
  {
      super(props);
      this.locationSelect = React.createRef();
      this.params = {};
      this.state = {
          
          user: {},
          customer_name: '',
          numBoxes:'',
          jobLocation:'',
          jobDispatch:'Archive',
          submitted: false,
          loading: false,
      };
    

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(e) 
  {
      const { name, value } = e.target;
      this.setState({ [name]: value });
  }

    handleSubmit(e)
    {
        e.preventDefault();
        this.setState({ submitted: true });
        const { customer_id, numBoxes, jobLocation, jobDispatch, returnUrl } = this.state;
        alert(this.state.jobLocation)
        // stop here if form is invalid
        if (!(numBoxes))
        {
            
            return;
        }
        this.setState({ loading: true });
        
        this.addJob(this.props.match.params.id, numBoxes, jobLocation, jobDispatch)
        .then(
            response => {
                this.setState({loading: false});
            },
            error => this.setState({ error, loading: false })
        );
        var path = "/customer/"+this.props.match.params.id
        this.props.history.push(path);
    }

  render(){
    var path = "/customer/"+this.props.match.params.id
    const { user, customerID, numBoxes, jobLocation, otherLocation, submitted, loading } = this.state;

    return (
        
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <h2>{localStorage.getItem('customer_name') }
            </h2>
            <p>Add New Job</p>
          </header>
          <div className="content">
          <form className={useStyles.root} autoComplete="off" onSubmit={this.handleSubmit}>
            <FormControl variant="outlined" className={useStyles.formControl}>
              <InputLabel ref={this.locationSelect} htmlFor="outlined-location-simple">
                Location
              </InputLabel>
              <Select
                value={this.state.jobLocation}
                onChange={this.handleChange}
                labelWidth={120}
                inputProps={{
                  name: 'location',
                  id: 'outlined-location-simple',
                }}
              >
                <MenuItem value={"Loading Dock"}>Loading Dock</MenuItem>
                <MenuItem value={"Front Door"}>Front Door</MenuItem>
                <MenuItem value={"Staging Area"}>Staging Area</MenuItem>
              </Select>
            </FormControl>
            <div className="form-group">
                <button className="btn btn-primary">Create</button>
            </div>
          </form>
          </div>
          
        </div>
      );
    }

    addJob(customer_id, job_numboxes, job_location, job_dispatch)
    {
      var path = "http://localhost:52773/BoxTracker/jobs"
        const requestOptions = {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
                },
            body: JSON.stringify({'customer_id': customer_id, 'job_numboxes': job_numboxes, 'job_location': job_location, 'operator_id':localStorage.getItem("operator_id"), 'job_dispatch': job_dispatch})
            };
        return fetch(path, requestOptions)
        .then(this.handleResponse).then(response => {
                if(response)
                {   
                    alert("Job Successfully Created")
                }
                return response;
            }
        )
            
    }
  }
  

export default AddJob;
