import React from 'react';
import ReactDOM from 'react-dom';
import AddCustomer from './AddCustomer'
import ViewCustomers from './ViewCustomers'
import Menu from 'rc-menu'
import './Menu.css';

class UserMenu extends React.Component{
  constructor(props)
  {
      super(props);
      this.state = {
          user: {}
      };
  }

  routeAddCustomer() {
    ReactDOM.render(<AddCustomer />, document.getElementById('root'))
  }
  routeViewCustomers() {
    ReactDOM.render(<ViewCustomers />, document.getElementById('root'))
  }

  render(){
      var menuStyle = {
        margin: 'auto',
        padding: 50,
       // width: 700,
        height: 400,
        overflow: 'auto',
      };
    const { user } = this.state;
    return (
      <div>
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>Welcome, user</p>
          </header>
          </div>
          <div className="Menu-body">
          <p>Main Menu</p>
          <Menu style={menuStyle}>
            <button onClick={this.routeAddCustomer}  className="btn btn-primary">Add Customer</button>
            <button onClick={this.routeViewCustomers}  className="btn btn-primary">View Customers</button>
          </Menu>
            
        </div>
      </div>
      );
    }
  }
  

export default UserMenu;
