import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

export default function SimpleSelect() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    location: ''
  });

  const locationSelect = React.createRef();
  const [labelWidth, setLabelWidth] = React.useState(0);
  React.useEffect(() => {
    setLabelWidth(locationSelect.current.offsetWidth);
  }, []);

  function handleChange(event) {
    setValues(oldValues => ({
      ...oldValues,
      [event.target.name]: event.target.value,
    }));
  }

  return (
    <form className={classes.root} autoComplete="off">
      <FormControl variant="outlined" className={classes.formControl}>
        <InputLabel ref={locationSelect} htmlFor="outlined-location-simple">
          Location
        </InputLabel>
        <Select
          value={values.location}
          onChange={handleChange}
          labelWidth={labelWidth}
          inputProps={{
            name: 'location',
            id: 'outlined-location-simple',
          }}
        >
          <MenuItem value={"Loading Dock"}>Loading Dock</MenuItem>
          <MenuItem value={"Front Door"}>Front Door</MenuItem>
          <MenuItem value={"Staging Area"}>Staging Area</MenuItem>
        </Select>
      </FormControl>
      <div className="form-group">
              <button className="btn btn-primary">Create</button>
          </div>
    </form>
  );
}
