import React from 'react';
import ReactDOM from 'react-dom';
import { withRouter } from "react-router";
import jobService from '../Services/jobService';
import Menu from './UserMenu';
import PropTypes from 'prop-types';
import '../Menu.css';

const propTypes ={
  location: PropTypes.object.isRequired
}
class AddJob extends React.Component{
 
  constructor(props)
  {
      super(props);
      
      this.params = {};
      this.state = {
          
          user: {},
          customer_name: '',
          numBoxes:'',
          submitted: false,
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e)
    {
        e.preventDefault();
        this.setState({ submitted: true });
        const { customer_name, returnUrl } = this.state;
  
        // stop here if form is invalid
        if (!(customer_name))
        {
            return;
        }
  
//        this.setState({ loading: true });
//        customerService.addCustomer(customer_name)
//        .then(
//            response => {
//                this.setState({loading: false});
//                this.routeMenu()
//            },
//            error => this.setState({ error, loading: false })
//        );
    }
  
  render(){
    alert(this.props.location.pathname.params[1])
    const { user, customerID, numBoxes, submitted, loading } = this.state;
    return (
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <h2></h2>
            <p>Add New Job</p>
          </header>
          <div className="Menu-body">
          <form name="form" onSubmit={this.handleSubmit}>
          <div className={'form-group' + (submitted && !customerID ? ' has-error' : '')}>
              <label htmlFor="numBoxes">Number of Boxes:</label>
              <input
                type="text"
                className="form-control"
                name="numBoxes"
                value={numBoxes}
                onChange={this.handleChange} />
          </div>
          <div className="form-group">
              <button className="btn btn-primary" disabled={loading}>Create</button>
              {loading &&
                  <img src="data:image/gif;base64," />
              }
          </div>
          </form>
          </div>
          
        </div>
      );
    }
  }
  

export default AddJob;
