import React, {Fragment} from 'react';
import ReactDOM from 'react-dom';

const ModalRoot = document.getElementById("modal-root");

export class ConfirmWidget extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            modal: false
        }
        this.handleModalOpen = () => this.setState({modal: true})
        this.handleModalClose = () => this.setState({modal: false})
    }

    render()
    {
        return ReactDOM.createPortal(
        <Fragment>
            {
                !this.state.modal && (
                    <button className="btn btn-primary" ref="focusButton" onClick={this.handleModalOpen}>Complete</button>
                )
            }
            {
                this.state.modal && (
                    <div>
                        <h1>Are You Sure?</h1><br/>
                        <button className="btn btn-primary" onClick={this.handleContinue}>YES</button>
                        <button className="btn btn-primary" ref="focusButton" onClick={this.handleModalClose}>NO</button>
                    </div>
                )
            }
        </Fragment>, ModalRoot);
    }

}
