import React from 'react';
import ReactDOM from 'react-dom';
import { JsonToTable } from "react-json-to-table";
import customerService from '../Services/customerService';
import UserMenu from './UserMenu';
import Menu, {Item as MenuItem} from 'rc-menu';
import '../Menu.css';
import '../customers.css';

class ViewCustomer extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          //customerid: '',
          user: {},
          customer: [],
          jobs: [],
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);

  }
  componentWillMount()
  {
    this.loadCustomer()
    this.state.refresh
    
  }
  loadCustomer(){
    customerService.getCustomerByID(this.props.location.pathname)
  }
  

  routeMenu() {
    ReactDOM.render(<UserMenu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }
    

  render(){
    var menuStyle = {
      margin: 'auto',
      padding: 40,
     // width: 700,
      height: 400,
      overflow: 'auto',
    };
    var customerStyle = {
        margin: 'auto',
        padding: 20,
        overflow: 'auto',
      };
    
    return (
        
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Customer</p>
          </header>
              <div style={customerStyle}><JsonToTable json={JSON.parse(localStorage.customer)}/></div>
              <Menu style={menuStyle}>
              <JsonToTable json={JSON.parse(localStorage.customerJobs)}/>
              </Menu>
          
        </div>
      );
    }
  }
  

export default ViewCustomer;
