import React from 'react';
import ReactTable from 'react-table';
import "react-table/react-table.css";
import '../Menu.css';

class scanBarcode extends React.Component{
  constructor(props)
  {
      super(props);
      this.state = {
          user: {},
          box:[],
          submitted: false,
          boxID:'',          
      };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }
    handleSubmit(e)
    {
        e.preventDefault();
        this.setState({ submitted: true });
        const { boxID, returnUrl } = this.state;
  
        // stop here if form is invalid
        if (!(boxID))
        {
            return;
        }
  
        this.setState({ loading: true });
        this.getBox(boxID)
        .then(
            response => {
                this.setState({loading: false});
            },
            error => this.setState({ error, loading: false })
        );
    }

  render(){
    
    const { user, box, boxID, submitted } = this.state;
    return (
      <div>
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>Welcome, {localStorage.getItem('username')}</p>
          </header>
            <div>
              <h3>Scanning Barcode:</h3>
              <form name="form" onChange={this.handlechange}>
                <div className={'form-group' + (submitted && !boxID ? ' has-error' : '')}>
                    <label htmlFor="boxID">Scan Box Barcode: </label>
                    <input
                      type="text"
                      className="form-control"
                      name="boxID"
                      value={boxID}
                      //onChange={this.handleChange}
                       />
                </div>
              </form>
            </div>
            <div>
            <ReactTable
              data={box}
              columns={[
                {
                  Header: "Box ID",
                  accessor: "box_id"
                },
                {
                  Header: "Job ID",
                  accessor:"job_id"
                },
                {
                  Header: "Customer",
                  accessor:"customer_name"
                },
                {
                  Header: "Location",
                  accessor:"box_location"
                },
                {
                  Header: "Operator",
                  accessor:"box_operator"
                },
                {
                  Header: "Step",
                  accessor:"box_step"
                },
                {
                  Header: "State",
                  accessor:"box_state"
                }
              ]}
              defaultPageSize={10}
              className="-striped -highlight"
            >
              {(state, makeTable) => {
                  return (
                    <div>
                      {makeTable()}
                    </div>
                  )
                }}
              </ReactTable>
            </div>
        </div>
      </div>
      );
    }

getBox(boxID)
{
    var path = "http://localhost:52773/csp/BoxTracker/boxes/"+boxID
    const requestOptions = {
        method: 'GET',
        headers: {
            'content-type': 'application/json',
            'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
            }
        };
    
    return fetch(path, requestOptions)
        .then(this.handleGetResponse)
        .then(response => {
            if (response) {
              this.setState({
                box: response
              })
          }
            return response;
        });
}
handleGetResponse(response) {
    
  return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
          const error = (data && data.message) || response.statusText;
          return Promise.reject(error);
      }
      return data;
  });
}
  }
  

export default scanBarcode;
