import React from 'react';
import ReactDOM from 'react-dom';
import { JsonToTable } from "react-json-to-table";
//import customerService from '../Services/customerService';
import UserMenu from './UserMenu';
import Menu, {Item as MenuItem} from 'rc-menu';
import '../Menu.css';
import '../customers.css';

class ViewCustomer extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          user: {},
          customer: {},
          customerJobs: [],
          jobs: [],
          loading: false
      };
    this.handleChange = this.handleChange.bind(this);

  }
  componentDidMount()
  {
    this.getCustomerByID(this.props.location.pathname)
  }
  

  routeMenu() {
    ReactDOM.render(<UserMenu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }
    

  render(){
    const {customer, customerJobs} = this.state
    var menuStyle = {
      margin: 'auto',
      padding: 40,
     // width: 700,
      height: 400,
      overflow: 'auto',
    };
    var customerStyle = {
        margin: 'auto',
        padding: 20,
        overflow: 'auto',
      };
    
    return (
        
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Customer</p>
          </header>
              <div style={customerStyle}>
                <JsonToTable json={customer}/>
              </div>
              <Menu style={menuStyle}>
              <JsonToTable json={customerJobs}/>
              </Menu>
          
        </div>
      );
    }
///
/// HTTP GET/POST METHODS
///
    addCustomer(customer_name)
{
    const requestOptions = {
        method: 'POST',
        headers: {
            'content-type': 'application/json',
            'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
            },
        body: JSON.stringify({'customer_name': customer_name})
        };
    return fetch("http://localhost:52773/csp/BoxTracker/customers", requestOptions)
    .then(this.handleResponse).then(response => {
            if(response)
            {   
                alert("Customer Successfully Created")
            }
            return response;
        }
    )
        
}

getCustomers()
{
    const requestOptions = {
        method: 'GET',
        headers: {
            'content-type': 'application/json',
            'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
            }
        };
    return fetch("http://localhost:52773/csp/BoxTracker/customers", requestOptions)
        .then(this.handleGetResponse)
        .then(response => {
            if (response) {
                localStorage.setItem('customers', JSON.stringify(response));
            }
            return response;
        });
}

getCustomerByID(customerID)
{
    var path = "http://localhost:52773/csp/BoxTracker"+customerID
    const requestOptions = {
        method: 'GET',
        headers: {
            'content-type': 'application/json',
            'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
            }
        };
    
    return fetch(path, requestOptions)
        .then(this.handleGetResponse)
        .then(response => {
            if (response) {
                let CustomerReturn = JSON.parse(JSON.stringify(response))
                let CDetails = JSON.stringify(CustomerReturn[0])
                let CJobs = JSON.stringify(CustomerReturn[1])
                
                this.setState({
                    customer: CDetails,
                    customerJobs: CJobs
                })
                //localStorage.setItem('customer', CustomerDetails);
                //localStorage.setItem('customerJobs', CustomerJobs)
            }
            return response;
        });
}

handleResponse(response) {
    
    return response.text().then(text => {
        const data = text && JSON.parse(text);
        if (!response.ok) {
            if (response.status === 401) {
                alert("not OK, 401")
                // auto logout if 401 response returned from api
                Location.reload(true);
            }

            const error = (data && data.message) || response.statusText;
            return Promise.reject(error);
        }
        return data;
    });
}

handleGetResponse(response) {
    
    return response.text().then(text => {
        const data = text && JSON.parse(text);
        if (!response.ok) {
            const error = (data && data.message) || response.statusText;
            return Promise.reject(error);
        }
        return data;
    });
}
  }
  

export default ViewCustomer;
