import React from 'react';
import ReactDOM from 'react-dom';
import { withRouter } from "react-router";
import jobService from '../Services/jobService';
import Menu from './UserMenu';
import PropTypes from 'prop-types';
import '../Menu.css';

const propTypes ={
  location: PropTypes.object.isRequired
}
class AddJob extends React.Component{
 
  constructor(props)
  {
      super(props);
      
      this.params = {};
      this.state = {
          
          user: {},
          customer_name: '',
          numBoxes:'',
          submitted: false,
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e)
    {
        e.preventDefault();
        this.setState({ submitted: true });
        const { customer_id, numBoxes, returnUrl } = this.state;
  
        // stop here if form is invalid
        if (!(numBoxes))
        {
            return;
        }
        alert(this.props.match.params.id)
        this.setState({ loading: true });
        
        //this.addJob(customer_id, numBoxes)
        //.then(
        //    response => {
        //        this.setState({loading: false});
        //        this.routeMenu()
        //    },
        //    error => this.setState({ error, loading: false })
        //);
    }
  
  render(){
    const { user, customerID, numBoxes, submitted, loading } = this.state;
    return (
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <h2>{localStorage.getItem('customer_name') }
            <br/>
            ID: {JSON.stringify(this.props.match.params.id)}
            </h2>
            <p>Add New Job</p>
          </header>
          <div className="Menu-body">
          <form name="form" onSubmit={this.handleSubmit}>
          <div className={'form-group' + (submitted && !customerID ? ' has-error' : '')}>
              <label htmlFor="numBoxes">Number of Boxes:</label>
              <input
                type="text"
                className="form-control"
                name="numBoxes"
                value={numBoxes}
                onChange={this.handleChange} />
          </div>
          <div className="form-group">
              <button className="btn btn-primary" disabled={loading}>Create</button>
              {loading &&
                  <img src="data:image/gif;base64," />
              }
          </div>
          </form>
          </div>
          
        </div>
      );
    }

    addJob(customer_id, job_numboxes)
    {
      var path = "http://localhost:52773/csp/BoxTracker/jobs"
        const requestOptions = {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
                },
            body: JSON.stringify({'customer_id': customer_id, 'job_numboxes': job_numboxes})
            };
        return fetch(path, requestOptions)
        .then(this.handleResponse).then(response => {
                if(response)
                {   
                    alert("Job Successfully Created")
                }
                return response;
            }
        )
            
    }
  }
  

export default AddJob;
