import React from "react";
import { Bar } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";

export default class LocationChart extends React.Component {
    constructor(props)
    {
        super(props);
        this.params = {};
        this.state = {
            stats: []
        };
      
    }

    componentWillMount()
    {
        this.getStats()
    }
    getLocationNums()
    {
        this.state.stats.map((data, i) => {
            let locationNums = []
            locationNums[0] = data.LocNums[0]
            locationNums[1] = data.LocNums[1]
            locationNums[2] = data.LocNums[2]
            locationNums[3] = data.LocNums[3]
            return locationNums
          })
          
    }
  chart = {
    dataBar: {
      labels: ["Preparation", "Scanning", "Quality Control", "Dispatch"],
      datasets: [
        {
          label: "No. Of Boxes",
          data: this.locationNums(),
          backgroundColor: [
            "rgba(113, 205, 205,0.4)",
            "rgba(98,  182, 239,0.4)",
            "rgba(255, 177, 101,0.4)",
            "rgba(255, 134,159,0.4)"
            
          ],
          borderWidth: 2,
          borderColor: [
            "rgba(113, 205, 205, 1)",
            "rgba(98,  182, 239, 1)",
            "rgba(255, 177, 101, 1)",
            "rgba(255, 134, 159, 1)"
            
          ]
        }
      ]
    },
    barChartOptions: {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        xAxes: [
          {
            barPercentage: 1,
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            }
          }
        ],
        yAxes: [
          {
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            },
            ticks: {
              beginAtZero: true
            }
          }
        ]
      }
    }
  }


  render() {
    return (
      <MDBContainer>
        <h3 className="mt-5">Number of Boxes Per Step</h3>
        <Bar data={this.chart.dataBar} options={this.chart.barChartOptions} />
      </MDBContainer>
    );
  }

getStats()
{
  var path = "http://localhost:52773/BoxTracker/stats"  
  const requestOptions = {
      method: 'GET',
      headers: {
          'content-type': 'application/json',
          'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
          }
      };
  return fetch(path, requestOptions)
      .then(this.handleGetResponse)
      .then(response => {
          if (response) {
              alert(JSON.stringify(response[1]))
            this.setState({
                stats : response
            })
          }
          return response;
      });
}

handleResponse(response) {
    
    return response.text().then(text => {
        const data = text && JSON.parse(text);
        if (!response.ok) {
            if (response.status === 401) {
                alert("not OK, 401")
                // auto logout if 401 response returned from api
                Location.reload(true);
            }

            const error = (data && data.message) || response.statusText;
            return Promise.reject(error);
        }
        return data;
    });
}

handleGetResponse(response) {
    
    return response.text().then(text => {
        const data = text && JSON.parse(text);
        if (!response.ok) {
            const error = (data && data.message) || response.statusText;
            return Promise.reject(error);
        }
        return data;
    });
}
}
