import React from 'react';
import ReactDOM from 'react-dom';
import { JsonToTable } from "react-json-to-table";
import UserMenu from './UserMenu';
import Menu, {Item as MenuItem} from 'rc-menu';
import '../Menu.css';

class ViewJobs extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          user: {},
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);

  }
  componentWillMount()
  {
    this.getJobs()
  }

  routeMenu() {
    ReactDOM.render(<UserMenu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    

  render(){
    const {jobs} = this.state
    var menuStyle = {
      margin: 'auto',
      padding: 50,
     // width: 700,
      height: 400,
      overflow: 'auto',
    };
    return (
        
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Jobs</p>
          </header>
              <Menu style={menuStyle}>
              <JsonToTable json={jobs} />
              </Menu>
          
        </div>
      );
    }

  getJobs()
  {
      const requestOptions = {
          method: 'GET',
          headers: {
              'content-type': 'application/json',
              'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
              }
          };
      return fetch("http://localhost:52773/csp/BoxTracker/jobs", requestOptions)
          .then(handleGetResponse)
          .then(response => {
              if (response) {
                  this.setState({
                    jobs: response
                  })
              }
              return response;
          });
  }
  }
  

export default ViewJobs;
