import React from 'react';
import ReactDOM from 'react-dom';
import ReactScrollList from 'react-scroll-list'
import customerService from './customerService';
import Menu from './Menu';
import './Menu.css';
import './customers.css';

class AddCustomer extends React.Component{
  constructor(props)
  {
      
      super(props);
      this.params = {};
      this.state = {
          user: {},
          customers: {},
          customerList: [],
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);

  }

  componentWillMount()
  {
    this.getCustomers()
  }

  getCustomers(){
    customerService.getCustomers()
  }

  routeMenu() {
    ReactDOM.render(<Menu />, document.getElementById('root'))
  }

    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }
  
  render(){
    const { user, customers, submitted, loading } = this.state;
    return (
        <div className="Menu">
            <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>View Customers</p>
          </header>
          <body>
            
            <ol className="customer">
                {
                    customerList.map(customer => (
                    <li key={customer.customer_id} align="start">
                    <div>
                    <p>{customer.customer_name}</p>
                    <p>{customer.customer_email}</p>
                    </div>
                    </li>
                    ))
                    }
            </ol>

          <button onClick={this.routeMenu}  className="btn btn-primary">Menu</button>
          </body>
        </div>
      );
    }
  }
  

export default AddCustomer;
