import React from 'react';
import ReactDOM from 'react-dom';

const portalRoot = document.getElementById('root');

export class ConfirmDialogue extends React.Component 
{
    state = { open: false };
  
    handleOpenButtonClick = () => this.setState({ open: true });
  
    handleCloseButtonClick = () => this.setState({ open: false });
  
    render() {
      return ReactDOM.createPortal(this.renderWidget(), portalRoot);
    }
  
    renderWidget() {
        return (
          <>
            { !this.state.open && (
                <button onClick={this.handleOpenButtonClick}>Complete</button>
              )
            }
            { this.state.open && (
                <div>
                    <h1>Are You Sure?</h1><br/>
                    <button onClick={this.completeStep()}>YES</button>
                    <button onClick={this.handleCloseButtonClick}>NO</button>
                </div>
              )
            }
          </>
        );
      }
    }
