import React from "react";
import { Bar } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";

export default class LocationChart extends React.Component {
    constructor(props) {

        super(props);
        this.params = {};
        this.state = {
          stats: []
        };
    }

    getLocationNums()
    {
        const { stats } = this.state
        stats.map((data, i) => {
            let locationNums = []
            locationNums[0] = data.LocNums[0]
            locationNums[1] = data.LocNums[1]
            locationNums[2] = data.LocNums[2]
            locationNums[3] = data.LocNums[3]
            this.setState({
                stats:  locationNums
            })
          })
          
    }
  chart = {
    dataBar: {
      labels: ["Preparation", "Scanning", "Quality Control", "Dispatch"],
      datasets: [
        {
          label: "No. Of Boxes",
          data: [12, 19, 3, 5],
          backgroundColor: [
            "rgba(113, 205, 205,0.4)",
            "rgba(98,  182, 239,0.4)",
            "rgba(255, 177, 101,0.4)",
            "rgba(255, 134,159,0.4)"
            
          ],
          borderWidth: 2,
          borderColor: [
            "rgba(113, 205, 205, 1)",
            "rgba(98,  182, 239, 1)",
            "rgba(255, 177, 101, 1)",
            "rgba(255, 134, 159, 1)"
            
          ]
        }
      ]
    },
    barChartOptions: {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        xAxes: [
          {
            barPercentage: 1,
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            }
          }
        ],
        yAxes: [
          {
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            },
            ticks: {
              beginAtZero: true
            }
          }
        ]
      }
    }
  }


  render() {
    return (
      <MDBContainer>
        <h3 className="mt-5">Number of Boxes Per Step</h3>
        <Bar data={this.chart.dataBar} options={this.chart.barChartOptions} />
      </MDBContainer>
    );
  }
}
