//core React imports
import React from 'react';
import ReactTable from 'react-table';
//@material-react imports
import Toolbar from "@material-ui/core/Toolbar";
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
//chart
import { Bar } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";
//App imports
import Header from "./../Components/Headers/Header";
import HeaderLinks from "./../Components/Headers/HeaderLinks";
import ContinueDialogue from './ContinueDialogue';
import CompletedChart from './../Components/Charts/BoxesCompletedChart';
//import LocationChart from './../Components/Charts/BoxLocationsChart';
//css imports
import "react-table/react-table.css";
import '../customers.css';
import '../Menu.css';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
  }
}))

export default class Dashboard extends React.Component {
  constructor(props) {

    super(props);
    this.params = {};
    this.state = {
      user: {},
      boxes: [],
      stats: [],
      loading: false,
      go: false,
      show: false,
      isShown: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  componentDidMount() {
    //ReactDOM.findDOMNode(this.refs.divFocus).focus();
    this.getBox()
    this.getStats()
  }

  componentDidUpdate() {
  }

  handleChange(e) {
    this.setState({ value: e.target.value });
  }

  handleSubmit(e) {
    this.setState({
      value: "",
      go: true
    })
    e.preventDefault();
  }

  render() {
    
    const { show, isShown, boxes, stats, boxID } = this.state
    var menuStyle = {
      margin: 'auto',
      padding: 40,
      // width: 700,
      height: 400,
      overflow: 'auto',
    };
    var customerStyle = {
      margin: 'auto',
      padding: 20,
      overflow: 'auto',
    };
    const { ...rest } = this.props;
    
    
    return (
      <div classname={useStyles.root}>
        <div className="Menu">
          <Header
            absolute
            fixed
            color="dark"
            brand="Box Tracker"
            rightLinks={<HeaderLinks />}
            {...rest}
          />
          <Toolbar />
          <header className="Menu-header">Dashboard</header>
          <h2>Average Job Completion Time: 
            {stats.map((data, i) => {
              return (
                <div>
                  {data.average_job_length}
                </div>
              )
            })
            }
            </h2>
          <Paper className={useStyles.paper}>
            <Grid container justify={"center"}>
              <Grid>
                <StepChart />
              </Grid>
            </Grid>
          </Paper>
          
        </div>
      </div>
    );
  }
  ///
  /// HTTP GET/POST METHODS
  ///
  getBox() {
    //alert(boxID)
    //boxID = 6
    var path = "http://localhost:52773/BoxTracker/boxes"
    //TEMPORARY
    //jobID = "1"
    const requestOptions = {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        'authorization': 'Basic U3VwZXJVc2VyOlBBU1M=',
      }
    };
    return fetch(path, requestOptions)
      .then(this.handleGetResponse)
      .then(response => {
        if (response) {
          //alert(JSON.stringify(response))
          this.setState({
            boxes: response
          })
        }
        return response;
      });
  }

  getStats() {
    var path = "http://localhost:52773/BoxTracker/stats"
    const requestOptions = {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        'authorization': 'Basic U3VwZXJVc2VyOlBBU1M=',
      }
    };
    return fetch(path, requestOptions)
      .then(this.handleGetResponse)
      .then(response => {
        if (response) {
          this.setState({
            stats: response
          })
        }
        return response;
      });
  }

  handleResponse(response) {

    return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
        if (response.status === 401) {
          alert("not OK, 401")
          // auto logout if 401 response returned from api
          Location.reload(true);
        }

        const error = (data && data.message) || response.statusText;
        return Promise.reject(error);
      }
      return data;
    });
  }

  handleGetResponse(response) {

    return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
        const error = (data && data.message) || response.statusText;
        return Promise.reject(error);
      }
      return data;
    });
  }

  
}

class StepChart extends React.Component {
  getStepNums()
  {

      this.state.stats.map((data, i) => {
          let StepNums = []
          StepNums[0] = data.LocNums[4]
          StepNums[1] = data.LocNums[5]
          StepNums[2] = data.LocNums[6]
          StepNums[3] = data.LocNums[7]
          return StepNums
        })
        
  }
chart = {
  dataBar: {
    labels: ["Preparation", "Scanning", "Quality Control", "Dispatch"],
    datasets: [
      {
        label: "No. Of Boxes",
        data: [12, 19, 3, 5],
        backgroundColor: [
          "rgba(113, 205, 205,0.4)",
          "rgba(98,  182, 239,0.4)",
          "rgba(255, 177, 101,0.4)",
          "rgba(255, 134,159,0.4)"
          
        ],
        borderWidth: 2,
        borderColor: [
          "rgba(113, 205, 205, 1)",
          "rgba(98,  182, 239, 1)",
          "rgba(255, 177, 101, 1)",
          "rgba(255, 134, 159, 1)"
          
        ]
      }
    ]
  },
  barChartOptions: {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      xAxes: [
        {
          barPercentage: 1,
          gridLines: {
            display: true,
            color: "rgba(0, 0, 0, 0.1)"
          }
        }
      ],
      yAxes: [
        {
          gridLines: {
            display: true,
            color: "rgba(0, 0, 0, 0.1)"
          },
          ticks: {
            beginAtZero: true
          }
        }
      ]
    }
  }
}
render() {
  alert(this.stats)
  return (
    <MDBContainer>
      <h3 className="mt-5">Number of Boxes Per Step</h3>
      <Bar data={this.chart.dataBar} options={this.chart.barChartOptions} />
    </MDBContainer>
  );
}
}
