import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Redirect} from 'react-router-dom';
import { createBrowserHistory } from "history";
import Menu from './UserMenu';
import '../Menu.css';
import ViewCustomers from './ViewCustomers';

class AddCustomer extends React.Component{
  constructor(props)
  {
    const customHistory = createBrowserHistory();
      super(props);
      this.params = {};
      this.state = {
          user: {},
          customer_name: '',
          customer_email:'',
          submitted: false,
          loading: false
      };
    

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
    handleChange(e) 
    {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e)
    {
        e.preventDefault();
        this.setState({ submitted: true });
        const { customer_name,customer_email, returnUrl } = this.state;
  
        // stop here if form is invalid
        if (!(customer_name))
        {
            return;
        }
  
        this.setState({ loading: true });
        this.addCustomer(customer_name, customer_email)
        .then(
            response => {
                this.setState({loading: false});
            },
            error => this.setState({ error, loading: false })
        );
        BrowserRouter(customHistory)
    }
  
  render(){
    const { user, customer_name,customer_email, submitted, loading } = this.state;
    return (
        <div className="Menu">
          <header className="Menu-header">
            <h1>Box Tracker</h1>
            <p>Add New Customer</p>
          </header>
          <div className="Menu-body">
          <form name="form" onSubmit={this.handleSubmit}>
          <div className={'form-group' + (submitted && !customer_name ? ' has-error' : '')}>
              <label htmlFor="customer_name">Customer Name:</label>
              <input
                type="text"
                className="form-control"
                name="customer_name"
                value={customer_name}
                onChange={this.handleChange} />
          </div>
          <div className={'form-group' + (submitted && !customer_name ? ' has-error' : '')}>
              <label htmlFor="customer_email">Customer Email:</label>
              <input
                type="text"
                className="form-control"
                name="customer_email"
                value={customer_email}
                onChange={this.handleChange} />
                
          </div>
          <div className="form-group">
              <button className="btn btn-primary" disabled={loading}>Create</button>
              {loading &&
                  <img src="data:image/gif;base64," />
              }
          </div>
          </form>
          </div>
          
        </div>
      );
    }

    addCustomer(customer_name, customer_email)
    {
        const requestOptions = {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'authorization':'Basic U3VwZXJVc2VyOlBBU1M=',
                },
            body: JSON.stringify({'customer_name': customer_name, 'customer_email': customer_email})
            };
        return fetch("http://localhost:52773/csp/BoxTracker/customers", requestOptions)
        .then(this.handleResponse).then(response => {
                if(response)
                {   
                    alert("Customer Successfully Created")
                }
                return response;
            }
        )
            
    }
  }
  

export default AddCustomer;
